document.addEventListener("DOMContentLoaded",(()=>{fetch("http://localhost:3000/api/birthdays").then((t=>t.json())).then((t=>{const e=document.querySelectorAll(".block_birthday_players");t.forEach(((t,o)=>{e[o]&&(e[o].querySelector(".name_player").textContent=t.name,e[o].querySelector(".data_title").textContent=t.birthday,e[o].querySelector(".timer").textContent=`Через ${t.days_left} дней`)}))})).catch((t=>{console.error("Ошибка загрузки дней рождения:",t)}))}));