document.addEventListener("DOMContentLoaded",(()=>{fetch("https://api.fcintermoscow.com/api/...").then((e=>e.json())).then((e=>{const t=document.querySelectorAll(".block_birthday_players");e.forEach(((e,o)=>{t[o]&&(t[o].querySelector(".name_player").textContent=e.name,t[o].querySelector(".data_title").textContent=e.birthday,t[o].querySelector(".timer").textContent=`Через ${e.days_left} дней`)}))})).catch((e=>{console.error("Ошибка загрузки дней рождения:",e)}))}));